import React, { useState } from 'react'
// import PropTypes from 'prop-types'

export default function TextForm(props) {
    const handleUpper = () => {
        console.log("Upper Case was Clicked" + text);
        let newText = text.toUpperCase();
        setText(newText);
    }
    const handleLower = () => {
        console.log("Lower Case was Clicked" + text);
        let newText = text.toLowerCase();
        setText(newText);
    }
    const handleclear = () => {
        console.log("Clear was Clicked" + text);
        let newText = '';
        setText(newText);
    }
    const handleOnChange = (event) => {
        console.log("On Change");
        setText(event.target.value);
    }
    const [text, setText] = useState('');
    return (
        <>
            <div className="container" style={{color : props.mode==='dark'?'white':'black'}}>
                <h1>{props.heading}</h1>
                <div className="mb-3">
                    {/* <label for="myBox" className="form-label">Example textarea</label> */}
                    <textarea className="form-control fs-5" id="myBox" rows="8" value={text} onChange={handleOnChange} 
                    style={
                        {backgroundColor : props.mode==='dark'?'#383b3e':'white' , 
                        color : props.mode==='dark'?'white':'black'}
                        }>
                    </textarea>
                    <button className="btn btn-primary m-2" onClick={handleUpper}>UpperCase</button>
                    <button className="btn btn-primary m-2" onClick={handleLower}>LowerCase</button>
                    <button className="btn btn-primary m-2" onClick={handleclear}>Clear</button>
                </div>
            </div>
            <div className="container" style={{color : props.mode==='dark'?'white':'black'}}>
                <h2>Your Text Summary</h2>
                <p className='fs-5'>
                    {text.split(" ").length} Words <br />
                    {text.length} characters <br />
                    {0.008 * text.split(" ").length} Minutes To read
                </p>
                <h2>Preview</h2>
                <p className='class="fst-italic"'><i>{text.length>0?text:"Enter something in the textbox to preview it here"}</i></p>

            </div>
        </>
    )
}
